Page._content:
/* 全局样式调整 */
:root {
  --content-width: 90%;
  --font-size-base: 0.95rem;
}

/* 全局缩放 */
html {
  zoom: 0.9;
  -ms-zoom: 0.9;
  -webkit-zoom: 0.9;
  -moz-transform: scale(0.9);
  -moz-transform-origin: 0 0;
}

/* 适配移动端 */
@media screen and (max-width: 768px) {
  html {
    zoom: 1;
    -ms-zoom: 1;
    -webkit-zoom: 1;
    -moz-transform: scale(1);
  }
}

/* 调整内容区域宽度和间距 */
.container {
  width: var(--content-width);
  max-width: 1200px;
}

/* 调整文章内容字体大小 */
.post-content {
  font-size: var(--font-size-base);
  line-height: 1.8;
}

/* 调整导航栏大小 */
.navbar {
  padding: 0.8rem 1rem;
  background-color: rgba(255, 255, 255, 0.95);
}

.navbar-brand {
  font-size: 1.2rem;
}

.navbar .navbar-nav .nav-link {
  padding: 0.5rem 1rem;
  font-size: 0.95rem;
}

/* 调整页脚大小 */
.footer {
  padding: 1.5rem 0;
  font-size: 0.9rem;
  background-color: #f8f9fa;
}

/* 调整文章卡片大小 */
.index-card {
  margin-bottom: 2rem;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.05);
  transition: all 0.3s;
}

.index-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 8px 15px rgba(0, 0, 0, 0.1);
}

/* 首页横幅调整 */
.banner {
  height: auto !important;
  padding: 4rem 0;
}

/* 文章标题调整 */
.index-header {
  font-size: 1.3rem;
  margin: 1rem 0;
}

/* 文章摘要调整 */
.index-excerpt {
  margin: 1rem 0;
  font-size: 0.9rem;
  line-height: 1.7;
}

/* 标签和分类样式 */
.post-meta {
  font-size: 0.85rem;
  margin: 0.5rem 0;
}

/* 移动端适配 */
@media (max-width: 768px) {
  .container {
    width: 95%;
  }

  .navbar {
    padding: 0.5rem;
  }

  .banner {
    padding: 3rem 0;
  }

  .index-header {
    font-size: 1.2rem;
  }
}

/* 修复图片溢出问题 */
.index-img {
  max-width: 100%;
  height: auto;
  object-fit: cover;
}

/* 修复文章卡片间距 */
.row {
  margin-left: -15px;
  margin-right: -15px;
}

.col, [class*="col-"] {
  padding-left: 15px;
  padding-right: 15px;
} 

/* 导入 Playfair Display 字体 */
@import url('https://fonts.googleapis.com/css2?family=Playfair+Display:ital,wght@1,500&display=swap');

/* 隐藏滚动条（保留之前的设置） */
::-webkit-scrollbar {
    display: none;
}

/* 标语样式 */
.typed-cursor {
    display: none;
}

#banner .text-white {
    font-family: 'Times New Roman', Times, serif;
    font-style: italic;
    letter-spacing: 1px;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.2);
}

/* 适配 Firefox */
* {
    scrollbar-width: none;
}

/* 适配 IE */
* {
    -ms-overflow-style: none;
}

/* 保持页面可滚动 */
html, body {
    overflow-y: auto;
}

